import React, { useState } from 'react';
import { Box, Typography, TextField, Button, Alert } from '@mui/material';

const Login = () => {
  const [mobileNumber, setMobileNumber] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleLogin = () => {
    if (!/^\d{10}$/.test(mobileNumber)) {
      setError('Please enter a valid 10-digit mobile number.');
      setSuccess('');
      return;
    }
    setError('');
    setSuccess('Login successful! Verification code sent to your mobile.');
    // Simulate login API call here
  };

  return (
    <Box sx={{ textAlign: 'center', padding: '2rem', maxWidth: 400, margin: 'auto', marginTop: '5rem', backgroundColor: '#f9f9f9', borderRadius: '8px' }}>
      <Typography variant="h4" gutterBottom>
        Login
      </Typography>
      <TextField
        label="Mobile Number"
        variant="outlined"
        fullWidth
        value={mobileNumber}
        onChange={(e) => setMobileNumber(e.target.value)}
        sx={{ marginBottom: '1rem' }}
        inputProps={{ maxLength: 10 }}
        placeholder="Enter your mobile number"
      />
      <Button variant="contained" color="primary" fullWidth onClick={handleLogin}>
        Send OTP
      </Button>
      {error && <Alert severity="error" sx={{ marginTop: '1rem' }}>{error}</Alert>}
      {success && <Alert severity="success" sx={{ marginTop: '1rem' }}>{success}</Alert>}
    </Box>
  );
};

export default Login;
