// src/components/AboutUs.js
import React from 'react';
import './styles/AboutUs.css'; // Create this CSS file for custom styling

const AboutUs = () => {
  return (
    <div className="about-us">
      <h1>About Us</h1>
      <p>
        Welcome to our Tiffin Service! We are dedicated to providing delicious, home-cooked meals delivered
        right to your doorstep. Our mission is to bring you the taste of home, with a variety of options to
        suit every palate, whether you're a fan of vegetarian or non-vegetarian dishes.
      </p>
      <p>
        We use only the freshest ingredients to ensure every meal is both nutritious and flavorful. 
        Thank you for choosing us to satisfy your daily meal needs!
      </p>
    </div>
  );
};

export default AboutUs;
