import React from 'react';
import { Box, Typography, Grid, Card, CardMedia, CardContent } from '@mui/material';
import { Link } from 'react-router-dom';

const Home = () => {
  const services = [
    {
      id: 1,
      name: 'Veg Tiffin',
      image: '/images/veg_tiffin.jpg', // Local image path
      description: 'Delicious and healthy Veg Tiffin.',
    },
    //{
    //id: 2,
    //name: 'Non-Veg Thali',
    //image: 'https://via.placeholder.com/300',
    //description: 'Delicious non-veg dishes for food lovers.',
    //},
  ];

  return (
    <Box sx={{ textAlign: 'center', padding: '2rem', backgroundColor: '#f9f9f9' }}>
      {/* Welcome Section */}
      <Box sx={{ padding: '2rem', backgroundColor: '#fff', borderRadius: '8px', marginBottom: '2rem' }}>
        <Typography variant="h3" gutterBottom>
          Welcome to Tiffin Service
        </Typography>
        <Typography variant="h6" color="text.secondary" gutterBottom>
          Your daily meal partner for healthy and delicious food.
        </Typography>
        <Typography variant="body1" color="text.secondary" sx={{ marginTop: '1rem' }}>
          For assistance, call us at: <strong>+91 12345 67890</strong>
        </Typography>
      </Box>

      {/* Services Section */}
      <Typography variant="h4" gutterBottom>
        Our Services
      </Typography>
      <Grid container spacing={4} justifyContent="center">
        {services.map((service) => (
          <Grid item xs={12} sm={6} md={4} key={service.id}>
            <Card>
              <CardMedia
                component="img"
                height="200"
                image={service.image}
                alt={service.name}
              />
              <CardContent>
                <Typography variant="h6">{service.name}</Typography>
                <Typography variant="body2" color="text.secondary">
                  {service.description}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      {/* Call to Action Section removed */}
    </Box>
  );
};

export default Home;
